import time
import numpy as np
import streamlit as st
col1,col2,col3=st.columns(3)
#static
with col1:
    st.header('Cat')
    st.image('https://plus.unsplash.com/premium_photo-1673967831980-1d377baaded2?fm=jpg&q=60&w=3000&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MXx8Y2F0c3xlbnwwfHwwfHx8MA%3D%3D',width=200)
with col2:
    st.header('Dog')
    st.image('data:image/jpeg;base64,',width=200)
with col3:
    st.header('Owl')
    st.image('https://images.pexels.com/photos/53977/eagle-owl-raptor-falconry-owl-53977.jpeg?cs=srgb&dl=pexels-pixabay-53977.jpg&fm=jpg',width=200)

#Dynamic
n=st.number_input('How many Columns Do u want?',1,10)
cols=st.columns(n)
for col in cols:
    with col:
        st.image("https://plus.unsplash.com/premium_photo-1673967831980-1d377baaded2?fm=jpg&q=60&w=3000&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MXx8Y2F0c3xlbnwwfHwwfHx8MA%3D%3D",width=200)

